# Funkcja ekstrakcji sekwencji numerycznych z danych digraf√≥w
def extract_features(digraphs):
    # Mo≈ºna u≈ºyƒá jednej lub wiƒôcej cech np. [interval, press_time_diff]
    return np.array([
        [d["interval"], d["press_time_2"] - d["press_time_1"]]
        for d in digraphs
    ])

